import { Usecase } from "./base";
export class TransferUsecases extends Usecase {
    constructor(client, privateKey) {
        super(client);
        this.privateKey = privateKey;
    }
    /**
     * Retrieve all transfers
     */
    async getAll() {
        const response = await this.client.get("/transfers", {
            headers: { "X-Grant": this.privateKey },
        });
        return response.data;
    }
    /**
     * Initialize a tranfer request
     * @param transferData
     * @returns
     */
    async initialize(transferData) {
        const response = await this.client.post("/transfers", transferData, {
            headers: { "X-Grant": this.privateKey },
        });
        return response.data;
    }
    /**
     * Get a single transfer by reference
     * @param reference transaction reference
     * @returns
     */
    async get(reference) {
        const response = await this.client.get(`/transfers/${reference}`, {
            headers: { "X-Grant": this.privateKey },
        });
        return response.data;
    }
}
